import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import StarBorder from '@material-ui/icons/StarBorder';
const color={
    color: '#ABABAB'
  };
const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}));

export default function NestedList() {
  const classes = useStyles();
  const [open1, setOpen1] = React.useState(true);
  const [open2, setOpen2] = React.useState(false);
  const [open3, setOpen3] = React.useState(false);

  function handleClick() {
    setOpen1(!open1);
  }
  function handleClick() {
    setOpen2(!open2);
  }
  function handleClick() {
    setOpen3(!open3);
  }

  return (
    <List
      component="nav"
      aria-labelledby="nested-list-subheader"
      className={classes.root}
    >
      {/* Reciters */}
      <ListItem button onClick={handleClick} style={color}>
        <ListItemIcon style={color} >
        <i className="fas fa-microphone"/>
        </ListItemIcon>
        <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Reciters </Typography>}/>
        {open1 ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      {/* RECITERS LIST */}
      <Collapse in={open1} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
              
            </ListItemIcon>
            <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Reciters </Typography>} />
          </ListItem>
        </List>
      </Collapse>

 {/* translations */}
 <ListItem button onClick={handleClick} style={color}>
        <ListItemIcon style={color} >
        <i class="fas fa-list"></i>
        </ListItemIcon>
        <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Translations </Typography>}/>
        {open2 ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      
      
      {/* TRASNLATIONS LIST */}
      <Collapse in={open2} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
             </ListItemIcon>
            <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Translations Item </Typography>} />
          </ListItem>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
             </ListItemIcon>
            <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Translations Item 2</Typography>} />
          </ListItem>
        </List>
      </Collapse>

     
      {/* TOOLTIP DISPLAY */}
      <ListItem button onClick={handleClick} style={color}>
        <ListItemIcon style={color} >
        <i class="fas fa-globe-americas"></i> 
        </ListItemIcon>
        <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Tooltip </Typography>}/>
        {open3 ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      {/* Tooltip LIST */}
      <Collapse in={open3} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
             </ListItemIcon>
            <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Translations Item </Typography>} />
          </ListItem>
          <ListItem button className={classes.nested}>
            <ListItemIcon>
             </ListItemIcon>
            <ListItemText primary={<Typography style={{ color: '#ABABAB', marginLeft:"-20px" }}>Translations Item 2</Typography>} />
          </ListItem>
        </List>
      </Collapse>
    </List>
  );
}
