import React from "react";
import SideBar from "./menu.js";
import "./menu.css";
import logo from "./img/logo.png";
import Languages from "./LangDropdown";
import Fatiha from "./Fatiha";
import Verse from "./Verse";

const Head = {
  height: "70px"
};
const imgStyle = {
  padding: "15px",
  marginLeft: "60px",
  height: "65px",
  width: "156px"
};
const LangStyle = {
  position: "absolute",
  right: "20px",
  top: "15px",
  bottom: "20px",
  fontSize: "14px"
};
const FatihaStyle = {
  position: "absolute",
  left: "210px",
  top: "15px",
  bottom: "20px",
  fontSize: "14px",  
  };
  const VerseStyle = {
    position: "absolute",
    left: "320px",
    top: "15px",
    bottom: "20px",
    fontSize: "14px",  
    };
  
function Header(){
return (
<header id="hd" className=" header indexheader" style={Head}>
        <div>
          <SideBar /> 
          <img src={logo} alt="logo" style={imgStyle} />
         <a style={FatihaStyle}> <Fatiha/></a>
         <a style={VerseStyle}>
            <Verse />
          </a>
         <a style={LangStyle}>
            <Languages />
          </a>
            
       

        </div>
      </header>
);
}
export default Header;
