import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import "./Set.css";
const useStyles = makeStyles({
  list: {
    width: "350px",
  },
  fullList: {
    width: "auto",
  },
});

const color={
  color:"#ababa",
}
export default function TemporaryDrawer() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [side]: open });
  };

  const sideList = side => (
    <div style={color}
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
    >
       <div 
                  className="sidenavright-upper d-flex justify-content-between align-items-center"
                >
                  <h6>Settings</h6>
                  <a
                    href="javascript:void(0)"
                    className="closebtn"
                    id="sideNavTogglerInnerRight"
                  >
                    <i className="fas fa-times"></i>
                  </a>
                </div>
                
      <List  >
        <ListItem button>
                  <ListItemIcon> < i  class="fas fa-info-circle" ></i></ListItemIcon>
        <ListItemText disableTypography primary={<Typography style={{ color: '#FFFFFF' }}>MyTitle</Typography>}/>
        </ListItem>

          <ListItem button>
        <ListItemIcon><i class="fas fa-book-open"></i></ListItemIcon>
                    Reading
          </ListItem>

          <ListItem button>
          <ListItemIcon> 
            <i className="fas fa-lightbulb"></i></ListItemIcon>
                 Nightmode
                   </ListItem>
          
          
        {/* {['Surah Info', 'Reading', 'Reading', 'Nightmode'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))} */}
      </List>
      <Divider />
      <List>
        <ListItem button >
      
                   <ListItemIcon> <i className="fas fa-microphone"></i>
                    
                    </ListItemIcon>
                    Reciters
                    <span className="ml-auto">
                      <i className="fas fa-chevron-down"></i>
                    </span>
                 
          </ListItem>
          
      </List>
    
    </div>
  );

  
  return (
    <div>
     
    <a className="btn" onClick={toggleDrawer('right', true)}> Settings <i class="fas fa-cog"></i></a>
            <Drawer transitionDuration={800} anchor="right" open={state.right} onClose={toggleDrawer('right', false)}>
        {sideList('right')}
      </Drawer>
    </div>
  );
}
