import Info from "./SurahInfo";
import {InfoContext} from "./index";
import Reading from "./Reading";
import Body from "./SurahBody";

function Main(){
  const [Status]=useContext(InfoContext);
  return (
   <>
   {Status && <Info/>}
 <Body/>
                    <Reading/>
                    </>
            );

  }

  export default Main;
